import React, { useState } from "react";
import FacebookLogin from "react-facebook-login";
import GoogleLogin from "react-google-login";
import { Redirect } from "react-router-dom";
import "./Welcome.css";
import { connect } from "react-redux";
import { fngetLogin } from "../store/action/index";

function Welcome(props) {
  console.log(props);
  const [redirect, setRedirect] = useState(false);

  const signup = (res, type) => {
    console.log(res.email);
    let postData;
    if (type === "facebook") {
      postData = {
        name: res.name,
        provider: type,
        email: res.email,
        provider_id: res.id,
        token: res.accessToken,
        provider_pic: res.picture.data.url,
      };
    }

    if (type === "google") {
      postData = {
        name: res.Tt.Bd,
        provider: type,
        email: res.profileObj.email,
        provider_id: res.googleId,
        token: res.accessToken,
        provider_pic: res.profileObj.imageUrl,
      };
    }

    if (postData) {
      sessionStorage.setItem("userData", JSON.stringify(postData));
      console.log("true");
      setRedirect(true);
    } else {
      console.log("hi");
    }
  };

  const responseFacebook = (response) => {
    console.log("facebook console");
    console.log(response);
    signup(response, "facebook");
    props.fnLogin(response);
  };

  const responseGoogle = (response) => {
    console.log("google console");
    console.log(response);
    signup(response, "google");
  };

  if (redirect || sessionStorage.getItem("userData")) {
    return <Redirect to={"/home"} />;
  }

  return (
    <div>
      <FacebookLogin
        appId="1015016365581153"
        autoLoad={false}
        fields="name,email,picture"
        callback={responseFacebook}
      />
      <br />
      <br />

      <GoogleLogin
        clientId="681084349248-r4e15s99nabjgtpp3bjffd8ba3l5itu8.apps.googleusercontent.com"
        buttonText="Login with Google"
        onSuccess={responseGoogle}
        onFailure={responseGoogle}
      />
    </div>
  );
}

const mapStateToprops = (state) => {
  console.log("state>>>", state);
  return {
    isLogin: state.isLoggedin,
  };
};

const mapDispatchToProps = {
  fnLogin: fngetLogin,
};
export default connect(mapStateToprops, mapDispatchToProps)(Welcome);
