import { AUTH_LOGIN } from "../../constant/type";
const initialState = {
  isLoggedin: false,
  userInfo: [],
  uid: "admin",
  password: "admin",
};

const Reducer = (state = initialState, action) => {
  console.log(action);
  console.log("action", action);
  switch (action.type) {
    case AUTH_LOGIN:
      sessionStorage.setItem("userData", JSON.stringify(action.payload.data));
      return {
        ...state,
        isLoggedin: true,
        userInfo: action.payload,
      };
    default:
      return {
        ...state,
      };
  }
};

export default Reducer;
