import React, { useState } from "react";
import FacebookLogin from "react-facebook-login";
import GoogleLogin from "react-google-login";
import { Redirect, Link } from "react-router-dom";
import { Col, Form, FormGroup, Label, Input, Button } from "reactstrap";
import "./Welcome.css";
// import { useDispatch } from "react-redux";

function Welcome(props) {
  console.log(props);
  const [redirect, setRedirect] = useState(false);

  // const dispatch = useDispatch();

  // const fnLogin = () => {
  //   dispatch({ type: "AUTH_LOGIN" });
  // };

  const signup = (res, type) => {
    console.log(res.email);
    let postData;
    if (type === "facebook") {
      postData = {
        name: res.name,
        provider: type,
        email: res.email,
        provider_id: res.id,
        token: res.accessToken,
        provider_pic: res.picture.data.url,
      };
    }

    if (type === "google") {
      postData = {
        name: res.Tt.Bd,
        provider: type,
        email: res.profileObj.email,
        provider_id: res.googleId,
        token: res.accessToken,
        provider_pic: res.profileObj.imageUrl,
      };
    }

    if (postData) {
      sessionStorage.setItem("userData", JSON.stringify(postData));
      console.log("true");
      setRedirect(true);
    } else {
      console.log("hi");
    }
  };

  const responseFacebook = (response) => {
    console.log("facebook console");
    console.log(response);
    signup(response, "facebook");
    // fnLogin(response);
  };

  const responseGoogle = (response) => {
    console.log("google console");
    console.log(response);
    signup(response, "google");
  };

  if (redirect || sessionStorage.getItem("userData")) {
    return <Redirect to={"/home"} />;
  }

  return (
    <section className="login_container">
      <div className="container-fluid h-100">
        <div className="row h-100">
          <Col sm="4" className="login-container">
            <div>
              <h1>Login</h1>
              <Form>
                <FormGroup>
                  <Label for="username">Username</Label>
                  <Input
                    type="text"
                    value=""
                    name="name"
                    id="username"
                    placeholder="Username"
                    required
                    onChange={""}
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="examplePassword">Password</Label>
                  <Input
                    type="password"
                    value={""}
                    name="password"
                    id="examplePassword"
                    placeholder="********"
                    required
                    onChange={""}
                  />
                </FormGroup>
                <FormGroup>
                  <Button className="login_btn" type="submit">
                    SIGN IN
                  </Button>
                  <p>
                    Don't have account ! <Link to="/register">Register </Link>{" "}
                  </p>
                </FormGroup>
              </Form>
              <div>
                <FacebookLogin
                  // className="loginBtn"
                  appId="1015016365581153"
                  autoLoad={true}
                  fields="name,email,picture"
                  onClick={""}
                  callback={""}
                />

                <GoogleLogin
                  className="loginBtn"
                  clientId="542036815989-783shn85jdkdfoesfuda3vcqim1mkrvp.apps.googleusercontent.com"
                  buttonText=""
                  onSuccess={""}
                  onFailure={""}
                  cookiePolicy={"single_host_origin"}
                />
              </div>
            </div>
          </Col>
        </div>
      </div>
    </section>

    // <div>
    //   <FacebookLogin
    //     appId="1015016365581153"
    //     autoLoad={false}
    //     fields="name,email,picture"
    //     callback={responseFacebook}
    //   />
    //   <br />
    //   <br />

    //   <GoogleLogin
    //     clientId="681084349248-r4e15s99nabjgtpp3bjffd8ba3l5itu8.apps.googleusercontent.com"
    //     buttonText="Login with Google"
    //     onSuccess={responseGoogle}
    //     onFailure={responseGoogle}
    //   />
    // </div>
  );
}

export default Welcome;
