import React from "react";
import FacebookLogin from "react-facebook-login";
import GoogleLogin from "react-google-login";
import { Link } from "react-router-dom";
import { Col, Form, FormGroup, Label, Input, Button } from "reactstrap";
import "./Welcome.css";
import { useDispatch } from "react-redux";

function Welcome(props) {
  console.log("welcome screen", props);
  const dispatch = useDispatch();

  const fnLogin = (res) => {
    dispatch({ type: "AUTH_LOGIN", payload: res });
  };

  const responseFacebook = (response) => {
    console.log("facebook console");
    console.log(response);

    fnLogin = (event) => {
      event.preventDefault();
      const userData = this.props.userList;
      let loginUser =
        userData.length > 0
          ? userData.find(
              (user) =>
                user.name === this.state.name &&
                user.password === this.state.password
            )
          : undefined;
      if (loginUser === undefined) {
        alert("Invalied userid and passoword");
      } else {
        this.props.fnLogin(loginUser);
      }
    };
    const resp = { name: response.name, imageUrl: response.picture.data.url };
    // const sessionData = {
    //   isLoggedin: true,
    //   userInfo: resp,
    //   uid: "admin",
    //   password: "admin",
    // };
    // sessionStorage.setItem("userData", JSON.stringify(sessionData));
    fnLogin(resp);
  };

  const responseGoogle = (response) => {
    console.log("google console");
    console.log(response);
    fnLogin(response.profileObj);
  };
  // if (sessionStorage.getItem("userData")) {
  //   const data = JSON.parse(sessionStorage.getItem("userData"));
  //   console.log("dta", data);

  //   fnLogin(data.userInfo);
  //   return <Redirect to={"/home"} />;
  // }
  return (
    <section className="login_container">
      <div className="container-fluid h-100">
        <div className="row h-100">
          <Col sm="4" className="login-container">
            <div>
              <h1>Login</h1>
              <Form onSubmit={fnLogin}>
                <FormGroup>
                  <Label for="username">Username</Label>
                  <Input
                    type="text"
                    value=""
                    name="name"
                    id="username"
                    placeholder="Username"
                    required
                    onChange={(event) =>
                      setuserInfo({
                        name: event.target.value,
                        password: userInfo.password,
                      })
                    }
                  />
                </FormGroup>
                <FormGroup>
                  <Label for="examplePassword">Password</Label>
                  <Input
                    type="password"
                    value={""}
                    name="password"
                    id="examplePassword"
                    placeholder="********"
                    required
                    onChange={(event) =>
                      setuserInfo({
                        name: event.target.value,
                        password: userInfo.password,
                      })
                    }
                  />
                </FormGroup>
                <FormGroup>
                  <Button className="login_btn" type="submit">
                    SIGN IN
                  </Button>
                  <p>
                    Don't have account ! <Link to="/register">Register </Link>{" "}
                  </p>
                </FormGroup>
              </Form>
              <FacebookLogin
                // className="loginBtn"
                appId="1015016365581153"
                autoLoad={true}
                fields="name,email,picture"
                onClick={""}
                callback={responseFacebook}
              />
              <br />
              <h6>Or</h6>
              <br />
              <GoogleLogin
                className="loginBtn"
                clientId="681084349248-r4e15s99nabjgtpp3bjffd8ba3l5itu8.apps.googleusercontent.com"
                buttonText="login with google"
                onSuccess={responseGoogle}
                onFailure={responseGoogle}
                cookiePolicy={"single_host_origin"}
              />
            </div>
          </Col>
        </div>
      </div>
    </section>

    // <div>
    //   <FacebookLogin
    //     appId="1015016365581153"
    //     autoLoad={false}
    //     fields="name,email,picture"
    //     callback={responseFacebook}
    //   />
    //   <br />
    //   <br />

    //   <GoogleLogin
    //     clientId="681084349248-r4e15s99nabjgtpp3bjffd8ba3l5itu8.apps.googleusercontent.com"
    //     buttonText="Login with Google"
    //     onSuccess={responseGoogle}
    //     onFailure={responseGoogle}
    //   />
    // </div>
  );
}

export default Welcome;
